import React, { useState } from 'react';

const Login = () => {
    const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    // Add your login logic here
    console.log('Login clicked. Email:', email, 'Password:', password);
  };
  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2 style={{ fontSize: '24px', marginBottom: '20px' }}>Login</h2>
      <form
        onSubmit={handleLogin}
        style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <label style={{ marginBottom: '10px' }}>
          Email:
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{
              padding: '10px',
              margin: '5px',
              borderRadius: '5px',
              border: '1px solid #ccc',
              width: '200px',
            }}
            required
          />
        </label>

        <label style={{ marginBottom: '10px' }}>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{
              padding: '10px',
              margin: '5px',
              borderRadius: '5px',
              border: '1px solid #ccc',
              width: '200px',
            }}
            required
          />
        </label>

        <button
          type="submit"
          style={{
            padding: '10px',
            margin: '10px',
            borderRadius: '5px',
            border: 'none',
            background: '#4CAF50',
            color: 'white',
            cursor: 'pointer',
          }}
        >
          Login
        </button>
      </form>
    </div>
  )
}

export default Login
