    "pointer-events-none absolute left-3 top-0 mb-0 max-w-[90%] origin-[0_0] truncate text-neutral-500 transition-all duration-200 ease-out peer-focus:scale-[0.8] peer-focus:text-primary motion-reduce:transition-none dark:text-neutral-200 dark:peer-focus:text-primary",
  selectOption:
    "flex flex-row items-center justify-between w-full px-4 truncate select-none",
  selectOptionDefault: "bg-transparent text-gray-700 dark:text-gray-200",

  selectOptionDisabled:
    "cursor-default text-gray-400 bg-transparent dark:text-gray-400",
  selectOptionSelected: "bg-black/5 dark:bg-white/30",
  selectOptionActive: "bg-black/5 dark:bg-white/30",
  selectOptionNonDisabled:
    "hover:bg-black/5 dark:hover:bg-white/30 cursor-pointer",
  selectOptionsMultipleActive: "bg-black/5 dark:bg-white/30",
  selectOptionsOptGroup: "pl-7",
  selectOptionGroup: "group/opt pointer-none",
  selectOptionGroupLabel:
    "flex flex-row items-center w-full px-4 truncate bg-transparent text-black/50 select-none dark:text-gray-300",
  selectOptionIcon: "w-7 h-7 rounded-full",
  selectOptionSecondaryText:
    "block text-[0.8rem] text-gray-500 dark:text-gray-300",
};

export default SelectTheme;
import React, { useMemo } from "react";
import clsx from "clsx";
import type { SelectOptionListProps, SelectOptionState } from "./types";
import type { ExtendedSelectData } from "../types";

const OPTIONS_WITHOUT_GROUP = 0;

const TESelect: React.FC<SelectOptionListProps> = ({
  data,
  selectData,
  selectedElements,
  optionHeight,
  handleOptionClick,
  handleSelectAll,
  selectAll,
  selectAllLabel,
  multiple,
  activeElementIndex,
  search,
  noResultsText,
  theme = {},
}) => {
  // classes

  const optionClasses = ({
    disabled,
    selected,
    active,
    hidden,
    group,
  }: SelectOptionState) => {
    return clsx(
      theme.selectOption,
      !disabled && !selected && !active && theme.selectOptionDefault,
      !disabled && theme.selectOptionNonDisabled,
      disabled && theme.selectOptionDisabled,
      hidden && "hidden",
      selected && theme.selectOptionSelected,
      active && theme.selectOptionActive,
      group && theme.selectOptionsOptGroup
    );
  };

  //  group options

  const optionGroups = useMemo(() => {
    const groups = selectData.filter((el) => el.optgroup).map((el) => el.text);
    return groups;
  }, [selectData]);

  const groupedSelectData = useMemo(() => {
    let groupIndex = 0;
    const groupedData = selectData.map((el) => {
      if (el.optgroup) {
        groupIndex++;
      }
      return { ...el, groupIndex: groupIndex };
    });
    return groupedData;
  }, [selectData]);

  const isEmptyResults = useMemo(() => {
    return (
      selectData.filter((item) => !item.optgroup && !item.hidden).length === 0
    );
  }, [selectData]);

  // options content

  const singleOption = (item: ExtendedSelectData) => (
    <>
      <span className="group">
        {multiple && (
          <input
            className={theme.formCheckInput}
            type="checkbox"
            checked={selectedElements.includes(item.elementPosition)}
            readOnly
          />
        )}

        {item.text}
        {item.secondaryText && (
          <span className={theme.selectOptionSecondaryText}>
            {item.secondaryText}
          </span>
        )}
      </span>
      {item.icon && (
        <span>
          <img className={theme.selectOptionIcon} src={item.icon} />
        </span>
      )}
    </>
  );

  const createOptions = (group?: number) => {
    return groupedSelectData.map((el) => {
      if (!el.optgroup && group === el.groupIndex) {
        return (
          <div
            key={el.elementPosition}
            className={optionClasses({
              selected: selectedElements.includes(el.elementPosition),
              disabled: el.disabled || false,
              hidden: el.hidden || false,
              active: activeElementIndex === el.elementPosition,
              group: Boolean(el.groupIndex),
            })}
            style={{ height: `${optionHeight}px` }}
            role={"option"}
            aria-selected={selectedElements.includes(el.elementPosition)}
            aria-disabled={el.disabled}
            onClick={() => handleOptionClick(el)}
          >
            {singleOption(el)}
          </div>
        );
      }
    });
  };

  return (
    <>
      {multiple && selectAll && !isEmptyResults && (
        <div
          className={optionClasses({
            disabled: false,
            hidden: false,
            selected: !!(
              selectedElements.length ===
              data.filter((el) => !el.disabled).length
            ),
            active: activeElementIndex === -1,
            group: false,
          })}
          style={{ height: `${optionHeight}px` }}
          onClick={handleSelectAll}
          role="option"
          aria-selected={
            selectedElements.length === data.filter((el) => !el.disabled).length
          }
        >
          <span>
            <input
              className={theme.formCheckInput}
              type="checkbox"
              checked={
                data.filter((el) => !el.disabled && !el.optgroup).length ===
                selectedElements.length
              }
              role="option"
              readOnly
            />
            {selectAllLabel}
          </span>
        </div>
      )}
      <div className={theme.optionsList}>
        {createOptions(OPTIONS_WITHOUT_GROUP)}
        {!isEmptyResults &&
          optionGroups?.map((groupName, index) => {
            const options = createOptions(index + 1).filter(
              (option) => option !== undefined
            );
            if (options.length === 0) {
              return;
            }
            return (
              <div
                className={theme.selectOptionGroup}
                key={`select-option-group ${groupName} ${index}`}
              >
                <label
                  className={theme.selectOptionGroupLabel}
                  style={{ height: optionHeight }}
                >
                  {groupName}
                </label>
                {options}
              </div>
            );
          })}
      </div>

      {search && isEmptyResults && (
        <div className={theme.noResult} style={{ height: `${optionHeight}px` }}>
          {noResultsText}
        </div>
      )}
    </>
  );
};

export default TESelect;
export const LEFT_ARROW = "ArrowLeft";
export const UP_ARROW = "ArrowUp";
export const RIGHT_ARROW = "ArrowRight";
export const DOWN_ARROW = "ArrowDown";
export const HOME = "Home";
export const END = "End";
export const PAGE_UP = "PageUp";
export const PAGE_DOWN = "PageDown";
export const ENTER = "Enter";
export const SPACE = " ";
export const ESCAPE = "Escape";
export const TAB = "Tab";
export const BACKSPACE = "Backspace";
export const DELETE = "Delete";
import type { ExtendedSelectData } from './types';

export const isArraysEqual = (arr1: ExtendedSelectData[], arr2: ExtendedSelectData[]) => {
  // check if both arrays have the same length
  if (arr1.length !== arr2.length) {
    return false;
  }

  for (let i = 0; i < arr1.length; i++) {
    const obj1: any = arr1[i];
    const obj2: any = arr2[i];

    const keys1 = Object.keys(obj1);
    const keys2 = Object.keys(obj2);
    if (keys1.length !== keys2.length || !keys1.every((key) => keys2.includes(key))) {
      return false;
    }

    if (!keys1.every((key) => obj1[key] === obj2[key])) {
      return false;
    }
  }
  return true;
};
