import Recipe from "./Recipe";

const Home = ({ recipes, loading, error }) => {
  return (
    <div className="py-8 container mx-auto flex flex-wrap justify-center gap-10">
      {recipes.length === 0 && !loading && !error ? (
        <div>
          <p className="lg:text-4xl text-xl text-center text-red-300 font-semibold">
            Please search any recipe! nothing to show
          </p>
          <img
  className="min-h-0"
  src="/8791681.jpg"
  width="100"  // Set the width to your desired value
  height="100" // Set the height to your desired value
  alt="Description of the image"
/>


        </div>
      ) : null}
      {loading && <p>{error ? error : "loading..."}</p>}
      {recipes?.length > 0 &&
        recipes.map((recipe) => <Recipe recipe={recipe} key={recipe.id} />)}
    </div>
  );
};

export default Home;
